INSERT INTO `creature_text` VALUES ('34850', '1', '1', 'We protected Gilneas from the Scourge. We protected Gilneas during the Northgate rebellion. We will protect Gilneas from whatever this new threat may be.', '12', '0', '50', '0', '0', '19616', 'Text 2');
INSERT INTO `creature_text` VALUES ('34850', '2', '2', 'Stand ready, guards! We don\'t know how many intruders we\'re dealing with, but the Headlands are overrun and we\'re cut off from the harbor towns. Expect to be outnumbered.', '12', '0', '50', '0', '0', '19614', 'Text 3');
