INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES
(39034, '0', '0', 'Any Ladies and Gentlemen present should LEAVE NOW...for only REAL MEN AND WOMEN will be able to handle the gory spectacle about to unfold! Introducing the first fighter, the warrior of the Wastewander, the Ayatollah of Kaja-Cola.. THE GINORMUS!', '14', '0', '0', '0', '0', '0', 'Dealwell - First Fight'),
(39034, '1', '0', 'Our next fighter was found in the deepest tombs of ZulFarrak! Georged on cursed blood, raised from death countless times, he now knows only fury and violence! Dare you face the mindless wrath of...ZUMONGA!', '14', '0', '0', '0', '0', '0', 'Dealwell - Second'),
(39034, '2', '0', 'The faint of heart may wish to shield their eyes! Being brought into the ring now is a horror. captured from the dark tunnels of the Centipaar Hive! I give you the skittering death: SARINEXX!', '14', '0', '0', '0', '0', '0', 'Dealwell - Sarinexx'),
(39034, '3', '0', 'Into the Thunderdome, Kel! Theres no getting out until someones hit the sand. Lets start the show!', '12', '0', '0', '0', '0', '0', 'Dealwell - Kelsey');
