INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(56173, 0, 0, 'You have done NOTHING. I will tear your world APART.', 14, 0, 100, 0, 0, 26527, 'Madness of Deathwing - aggro'),
(56173, 1, 0, 'I AM DEATHWING, THE DESTROYER, THE END OF ALL THINGS, INEVITABLE, INDOMITABLE; I AM THE CATACLYSM!', 14, 0, 100, 0, 0, 26353, 'Madness of Deathwing - phase 2'),
(56173, 2, 0, 'There is no shelter from my fury.', 14, 0, 100, 0, 0, 26354, 'Madness of Deathwing - arrow 1'),
(56173, 2, 1, 'Your armor means nothing, your faith even less.', 14, 0, 100, 0, 0, 26355, 'Madness of Deathwing - arrow 2'),
(56173, 2, 2, 'The sea will swallow your smoldering remains.', 14, 0, 100, 0, 0, 26356, 'Madness of Deathwing - arrow 3'),
(56173, 3, 0, 'Mutated Corruption begins to impale the Aspect! Stop it!', 41, 0, 100, 0, 0, 0, 'Madness of Deathwing - Impale Aspect'),
(56173, 4, 0, '|TInterface\\Icons\\ability_deathwing_assualtaspects.blp:20|t %s attacks |cFFFF0000|Hspell:107018|h[Alexstrasza]|h|r!', 41, 0, 100, 0, 0, 0, 'Madness of Deathwing - Attack Alexstrasza'),
(56173, 5, 0, '|TInterface\\Icons\\ability_deathwing_assualtaspects.blp:20|t %s attacks |cFFFFFF00|Hspell:107018|h[Nozdormu]|h|r!', 41, 0, 100, 0, 0, 0, 'Madness of Deathwing - Attack Nozdormu'),
(56173, 6, 0, '|TInterface\\Icons\\ability_deathwing_assualtaspects.blp:20|t %s attacks |cFF04FC32|Hspell:107018|h[Ysera]|h|r!', 41, 0, 100, 0, 0, 0, 'Madness of Deathwing - Attack Ysera'),
(56173, 7, 0, '|TInterface\\Icons\\ability_deathwing_assualtaspects.blp:20|t %s attacks |cFF5858FA|Hspell:107018|h[Kalecgos]|h|r!', 41, 0, 100, 0, 0, 0, 'Madness of Deathwing - Attack Kalecgos');
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(56099, 0, 0, 'Excellent work. The fire of my heart glows with a brilliant purity unmatched; every spark of it I will channel into the Dragon Soul.', 14, 0, 100, 0, 0, 26497, 'Alexstrasza - dwbattle 1'),
(56099, 1, 0, 'I will cleanse whatever corruption I can; my fire will not harm you.', 14, 0, 100, 0, 0, 26498, 'Alexstrasza - dwbattle 2'),
(56099, 2, 0, 'He\'s completely mad!', 14, 0, 100, 0, 0, 26499, 'Alexstrasza - dwbattle 3'),
(56099, 3, 0, 'No! Such power! Deathwing\'s summoning of the final Cataclysm will destroy all life on Azeroth. Quickly, we must interrupt him!', 14, 0, 100, 0, 0, 26500, 'Alexstrasza - dwbattle 4'),
(56099, 4, 0, 'You have accomplished the impossible, succeeded where all Azeroth feared you would fail.', 14, 0, 100, 0, 0, 26501, 'Alexstrasza - dwvicrory 1'),
(56099, 5, 0, 'Before you is the Cache of the Aspects. Take from it what you will, for you have more than earned the right.', 14, 0, 100, 0, 0, 26502, 'Alexstrasza - dwvicrory 2');
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(56102, 0, 0, 'It is time. I will expend everything to bind every thread here, now, around the Dragon Soul. What comes to pass will NEVER be undone.', 14, 0, 100, 0, 0, 25948, 'Nozdormu - dwbattle 1'),
(56102, 1, 0, 'I will slow the Destroyer\'s attacks when I can.', 14, 0, 100, 0, 0, 25949, 'Nozdormu - dwbattle 2'),
(56102, 2, 0, 'Press the attack, heroes!', 14, 0, 100, 0, 0, 25950, 'Nozdormu - dwbattle 3'),
(56102, 3, 0, 'Hurry, heroes. In mere moments Deathwing\'s Cataclysm will complete what he begun and end the world. Join me in the attack, now!', 14, 0, 100, 0, 0, 25951, 'Nozdormu - dwbattle 4'),
(56102, 4, 0, '...although, you may want to hurry.', 14, 0, 100, 0, 0, 25952, 'Nozdormu - dwvictory 1');
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(56100, 0, 0, ' We are one step closer. The unknowable, transcendent power of the Emerald Dream I now give unto the Dragon Soul.', 14, 0, 100, 0, 0, 26141, 'Ysera - dwbattle 1'),
(56100, 1, 0, 'I will bring you closer to the Emerald Dream. Seek safety there when the fight becomes too intense.', 14, 0, 100, 0, 0, 26142, 'Ysera - dwbattle 2'),
(56100, 2, 0, 'Such rage I have never seen...', 14, 0, 100, 0, 0, 26143, 'Ysera - dwbattle 3'),
(56100, 3, 0, 'Deathwing is conjuring the final Cataclysm; even the Emerald Dream trembles. If we are to stop the spell, we must attack him together.', 14, 0, 100, 0, 0, 26144, 'Ysera - dwbattle 4');
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES
(56101, 0, 0, 'Well done! I will realign the flow of mana and fill the Dragon Soul with all my arcane might.', 14, 0, 100, 0, 0, 26258, 'Kalecgos - dwbattle 1'),
(56101, 1, 0, 'I will charge you with arcane energy to blast your foes.', 14, 0, 100, 0, 0, 26259, 'Kalecgos - dwbattle 2'),
(56101, 2, 0, 'Is he... coming apart?!', 14, 0, 100, 0, 0, 26260, 'Kalecgos - dwbattle 3'),
(56101, 3, 0, 'The Destroyer is gathering all his might for a blow that will split the world. Attack him, now! We must stop the final Cataclysm!', 14, 0, 100, 0, 0, 26261, 'Kalecgos - dwbattle 4');
