UPDATE locales_npc_text SET Text0_0_loc3='Seid gegrüßt. Ich bin Gnomenmagierlehrer und Ihr seid Magier.', Text0_1_loc3='Seid gegrüßt. Ich bin Gnomenmagierlehrerin und Ihr seid Magier.' WHERE ID=560;
UPDATE locales_npc_text SET Text0_0_loc3='Seid gegrüßt, $C. Ihr erbittet einen Rat? Freundet Euch mit einem Magier an! Wir haben da einen Trick oder zwei auf Lager, ja das haben wir...', Text0_1_loc3='Seid gegrüßt, $C. Ihr erbittet einen Rat? Freundet Euch mit einem Magier an! Wir haben da einen Trick oder zwei auf Lager, ja das haben wir...' WHERE ID=561;
