UPDATE locales_npc_text SET Text0_0_loc3='Seit dem Fall von Dalaran und der Zerstörung von Quel\'Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', Text0_1_loc3='Seit dem Fall von Dalaran und der Zerstörung von Quel\'Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', Text1_0_loc3='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.', Text1_1_loc3='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.' WHERE ID=1231;
