UPDATE locales_npc_text SET Text0_0_loc3='Wohin des Weges, $GBursche:Mädel;? Wohin Ihr auch wollt, für ein paar Münzen bringen Euch meine Greife in Windeseile hin.', Text0_1_loc3='Wohin des Weges, $GBursche:Mädel;? Wohin Ihr auch wollt, für ein paar Münzen bringen Euch meine Greife in Windeseile hin.', Text1_0_loc3='Diese großen Wildtiere kennen Wege, die man zu Fuß nie und nimmer finden würde. Sie bringen Euch blitzschnell überallhin und zeigen Euch dabei womöglich noch etwas Neues.', Text1_1_loc3='Diese großen Wildtiere kennen Wege, die man zu Fuß nie und nimmer finden würde. Sie bringen Euch blitzschnell überallhin und zeigen Euch dabei womöglich noch etwas Neues.' WHERE ID=1256;
