UPDATE locales_npc_text SET Text0_0_loc3='Ihr sucht Grol\'dar und die Dunkelfeuerenklave? Sucht in der unterirdischen Höhle namens Kluft der Schatten danach. Es führen zwei Tunnel in diese Höhle hinab, einer südlich des Tals der Weisheit, einer von der Gasse.', Text0_1_loc3='Ihr sucht Grol\'dar und die Dunkelfeuerenklave? Sucht in der unterirdischen Höhle namens Kluft der Schatten danach. Es führen zwei Tunnel in diese Höhle hinab, einer südlich des Tals der Weisheit, einer von der Gasse.', Text1_0_loc3='Haben wir uns nicht gerade erst von der Brennenden Legion befreit, und schon lasst Ihr Euch wieder mit Dämonen ein? Die Dunkelfeuerenklave ist in der unterirdischen Höhle namens Kluft der Schatten zu finden. Von der Gasse aus führt ein Tunnel hinunter.', Text1_1_loc3='Haben wir uns nicht gerade erst von der Brennenden Legion befreit, und schon lasst Ihr Euch wieder mit Dämonen ein? Die Dunkelfeuerenklave ist in der unterirdischen Höhle namens Kluft der Schatten zu finden. Von der Gasse aus führt ein Tunnel hinunter.' WHERE ID=2564;
