UPDATE locales_npc_text SET Text0_0_loc3='Es geht nur um Waffen und Flammen, bis jemand verliert und stirbt.', Text0_1_loc3='Es geht nur um Waffen und Flammen, bis jemand verliert und stirbt.', Text1_0_loc3='Mein Vater sagte mir, dass solche Tage kommen würden, da ich fremden Leuten begegnen würde und mich hüten müsste, sie zu töten.', Text1_1_loc3='Mein Vater sagte mir, dass solche Tage kommen würden, da ich fremden Leuten begegnen würde und mich hüten müsste, sie zu töten.', Text2_0_loc3='Habt Ihr schon das Donnerbräu Lagerbier versucht? Das ist echt guter Stoff!', Text2_1_loc3='Habt Ihr schon das Donnerbräu Lagerbier versucht? Das ist echt guter Stoff!', Text3_0_loc3='In einer Stunde habe ich Dienst, aber bis dahin... hoch die Tassen!', Text3_1_loc3='In einer Stunde habe ich Dienst, aber bis dahin... hoch die Tassen!' WHERE ID=2637;
