UPDATE locales_npc_text SET Text0_0_loc3='Seht Ihr mich an, $GJüngelchen:Mädchen;?', Text0_1_loc3='Seht Ihr mich an, $GJüngelchen:Mädchen;?', Text1_0_loc3='Ich nehme einen Becher Dunkeleisenbier. Oh, seid Ihr nicht $Gder Kellner:die Kellnerin;?', Text1_1_loc3='Ich nehme einen Becher Dunkeleisenbier. Oh, seid Ihr nicht $Gder Kellner:die Kellnerin;?', Text2_0_loc3='Ich glaube, Stöpsel hat eines seiner Fässchen zu voll gemacht. Sieht aus, als würde es gleich explodieren!', Text2_1_loc3='Ich glaube, Stöpsel hat eines seiner Fässchen zu voll gemacht. Sieht aus, als würde es gleich explodieren!', Text3_0_loc3='Stört mich nicht beim Abfeiern...', Text3_1_loc3='Stört mich nicht beim Abfeiern...' WHERE ID=2758;
