UPDATE locales_npc_text SET Text0_0_loc3='Passt bloß auf, $C. Achtet unsere Bräuche, sonst möge Euch und Eure ganze Familie ein Fluch treffen!', Text0_1_loc3='Passt bloß auf, $C. Achtet unsere Bräuche, sonst möge Euch und Eure ganze Familie ein Fluch treffen!' WHERE ID=4440;
UPDATE locales_npc_text SET Text0_0_loc3='Ich tu, was ich kann, um Euch was über den Weg des Priesters zu erzählen, wenn\'s das ist, warum Ihr hier seid.', Text0_1_loc3='Ich tu, was ich kann, um Euch was über den Weg des Priesters zu erzählen, wenn\'s das ist, warum Ihr hier seid.' WHERE ID=4441;
