UPDATE locales_npc_text SET Text0_0_loc3='Auch wenn das Anhäufen von Reichtum längst nicht mehr die treibende Kraft in unserem Leben ist, haben wir nicht vergessen, dass Geld die Welt regiert. Das Auktionshaus im Sitz der Naaru.', Text0_1_loc3='Auch wenn das Anhäufen von Reichtum längst nicht mehr die treibende Kraft in unserem Leben ist, haben wir nicht vergessen, dass Geld die Welt regiert. Das Auktionshaus im Sitz der Naaru.' WHERE ID=9528;
