UPDATE locales_npc_text SET Text0_0_loc3='Ihr findet unsere Kampfmeister in der Halle des Lichts. Dort findet Ihr auch einen gewissen Miglik Klecksstrom. Er steht bei Versorger Breel und repräsentiert angeblich irgendeinen Arenakampfverband.', Text0_1_loc3='Ihr findet unsere Kampfmeister in der Halle des Lichts. Dort findet Ihr auch einen gewissen Miglik Klecksstrom. Er steht bei Versorger Breel und repräsentiert angeblich irgendeinen Arenakampfverband.', Text1_0_loc3='Ich werde den Ort auf Eurer Karte markieren.', Text1_1_loc3='Ich werde den Ort auf Eurer Karte markieren.' WHERE ID=9531;
