UPDATE locales_npc_text SET Text0_0_loc3='Willkommen in der Bank des königlichen Markts, $GSire:holde Dame;. Womit kann ich Euch heute dienen?', Text0_1_loc3='Willkommen in der Bank des königlichen Markts, $GSire:holde Dame;. Womit kann ich Euch heute dienen?', Text1_0_loc3='Seid gegrüßt! Womit kann die Bank des königlichen Markts Euch heute dienen?', Text1_1_loc3='Seid gegrüßt! Womit kann die Bank des königlichen Markts Euch heute dienen?' WHERE ID=9563;
