UPDATE locales_npc_text SET Text0_0_loc3='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: Geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', Text0_1_loc3='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: Geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.' WHERE ID=13846;
