UPDATE locales_npc_text SET Text0_0_loc3='Die Besucher der Horde halten sich hauptsächlich in Sonnenhäschers Zuflucht auf, im nordöstlichen Dalaran.$B$BDort gibt es ein Gasthaus, sowie Portale zu den verschiedenen Städten der Horde und den Schlachtfeldern.', Text0_1_loc3='Die Besucher der Horde halten sich hauptsächlich in Sonnenhäschers Zuflucht auf, im nordöstlichen Dalaran.$B$BDort gibt es ein Gasthaus, sowie Portale zu den verschiedenen Städten der Horde und den Schlachtfeldern.' WHERE ID=13972;
