UPDATE locales_npc_text SET Text0_0_loc3='In Dalaran gibt es kein Auktionshaus. Ihr müsst ein Portal zu einer Eurer Hauptstädte nehmen.$B$BSie können in Dalaran in den Vierteln der Allianz und der Horde gefunden werden: in der Silbernen Enklave und in Sonnenhäschers Zuflucht.', Text0_1_loc3='In Dalaran gibt es kein Auktionshaus. Ihr müsst ein Portal zu einer Eurer Hauptstädte nehmen.$B$BSie können in Dalaran in den Vierteln der Allianz und der Horde gefunden werden: in der Silbernen Enklave und in Sonnenhäschers Zuflucht.', Text1_0_loc3='Ich werde den Ort auf Eurer Karte markieren.', Text1_1_loc3='Ich werde den Ort auf Eurer Karte markieren.' WHERE ID=14010;
