UPDATE locales_npc_text SET Text0_0_loc3='Ein Tunnel führt in den untersten Teil der Stadt im Westen der Kanalisation, gerade nördlich der Schenke \'Zur Zauberkrähe\'.$B$BDiejenigen, die ein wenig abenteuerlustiger sind, benutzen diesen um Dalaran zu betreten und zu verlassen.', Text0_1_loc3='Ein Tunnel führt in den untersten Teil der Stadt im Westen der Kanalisation, gerade nördlich der Schenke \'Zur Zauberkrähe\'.$B$BDiejenigen, die ein wenig abenteuerlustiger sind, benutzen diesen um Dalaran zu betreten und zu verlassen.' WHERE ID=14020;
