UPDATE locales_npc_text SET Text0_1_loc3='Seid gegrüßt, $C.' WHERE ID=14327;
UPDATE locales_npc_text SET Text0_0_loc3='Seid Ihr sicher, dass Ihr für den berittenen Kampf bereit seid?', Text0_1_loc3='Seid Ihr sicher, dass Ihr für den berittenen Kampf bereit seid?' WHERE ID=14335;
UPDATE locales_npc_text SET Text0_0_loc3='Kommt näher, $C. Ich habe nicht viel Zeit...' WHERE ID=14336;
UPDATE locales_npc_text SET Text0_0_loc3='Der tödlich verwundete Orc scheint bewusstlos zu sein.' WHERE ID=14337;
UPDATE locales_npc_text SET Text0_0_loc3='<Der leblose Körper des Orcs reagiert nicht mehr...>' WHERE ID=14338;
UPDATE locales_npc_text SET Text0_1_loc3='Schön Euch zu sehen, $C.$B$BEntschuldigt... kann nicht reden. Wir sind hier ein wenig am Kämpfen.' WHERE ID=14339;
UPDATE locales_npc_text SET Text0_1_loc3='Lasst es mich einfach wissen, wenn Ihr bereit seid, mit einer Staffel loszuziehen.$B$BIch habe ein paar in Reserve, wenn Ihr also noch welche braucht, kommt wieder.' WHERE ID=14340;
UPDATE locales_npc_text SET Text0_0_loc3='Was gibt es, $C?' WHERE ID=14344;
UPDATE locales_npc_text SET Text0_0_loc3='Ich nicht arbeiten bis Elfens weg sind!', Text0_1_loc3='Ich nicht arbeiten bis Elfens weg sind!', Text1_0_loc3='Elfens töten zu viele Peons im Wald. Ich bleiben hier, wo es sicher.', Text1_1_loc3='Elfens töten zu viele Peons im Wald. Ich bleiben hier, wo es sicher.', Text2_0_loc3='Ich nicht sterben für bisschen Holz!', Text2_1_loc3='Ich nicht sterben für bisschen Holz!' WHERE ID=14346;
UPDATE locales_npc_text SET Text0_1_loc3='Seid gegrüßt, $C.' WHERE ID=14347;
