UPDATE locales_npc_text SET Text0_0_loc3='Diese Schattenwand wird die Dämonen lange genug zurückhalten, dass Ihr hinüberkönnt.$B$BWenn Ihr und die anderen Elfen hinübergelangt seid, werde ich Euch folgen.' WHERE ID=19024;
